import React, { useEffect, useState } from "react";
import { useRecoilValue } from "recoil";
import { userState } from "../atoms/userAtom";
import { useNavigate } from "react-router-dom";

export default function ArrangementPage() {
  // Ï†ÑÏó≠ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Î°úÎî©
  const user = useRecoilValue(userState);
  // Ï∂îÏ≤ú Ïó¨Ìñâ Îç∞Ïù¥ÌÑ∞ ÏÉÅÌÉú
  const [recommendedTrips, setRecommendedTrips] = useState([]);
  // Îç∞Ïù¥ÌÑ∞ Î°úÎî© ÏÉÅÌÉú
  const [loading, setLoading] = useState(true);
  // ÌéòÏù¥ÏßÄ Ïù¥ÎèôÏùÑ ÏúÑÌïú navigate Ìï®Ïàò
  const navigate = useNavigate();

  // Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ Ïãú ÏÇ¨Ïö©Ïûê IDÎ°ú Ï∂îÏ≤ú Ïó¨Ìñâ Îç∞Ïù¥ÌÑ∞ fetch
  useEffect(() => {
    if (!user?.id) return; // ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Í∞Ä ÏóÜÏúºÎ©¥ fetchÌïòÏßÄ ÏïäÏùå
    fetch(`${process.env.REACT_APP_API_URL}/api/travel/user/${user.id}`)
      .then((res) => res.json())
      .then((data) => {
        setRecommendedTrips(data); // fetchÎêú Ï∂îÏ≤ú Ïó¨Ìñâ Îç∞Ïù¥ÌÑ∞ Ï†ÄÏû•
        setLoading(false); // Î°úÎî© ÏôÑÎ£å
      })
      .catch((err) => {
        // fetch Ïã§Ìå® Ïãú ÏóêÎü¨ Ï≤òÎ¶¨
        console.error("Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", err);
        setLoading(false);
      });
  }, [user]);

  // Ï∂îÏ≤ú Ïó¨Ìñâ Î™©Î°ùÏùÑ Ïπ¥Îìú ÌòïÏãùÏúºÎ°ú Î†åÎçîÎßÅÌïòÎäî Ìï®Ïàò
  const renderTrips = (trips) => (
    <div className="grid gap-6 md:grid-cols-2 pb-10">
      {trips.map((trip) => (
        // Ï∂îÏ≤ú Ïó¨Ìñâ Ïπ¥Îìú: ÌÅ¥Î¶≠ Ïãú ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
        <div
          key={trip.id}
          onClick={() =>
            navigate(`/accessible-travel`, { state: { recommendation: trip } })
          }
          className="cursor-pointer border rounded-lg p-6 shadow-sm bg-white hover:shadow-md transition"
        >
          {/* Ïó¨Ìñâ Ï†úÎ™© */}
          <h2 className="text-2xl font-semibold text-green-600 mb-2">
            {trip.title}
          </h2>
          {/* Ïó¨Ìñâ ÏÑ§Î™Ö */}
          <p className="text-gray-700 mb-3">{trip.description}</p>
          {/* ÏÉùÏÑ±Ïùº Ï†ïÎ≥¥ */}
          <p className="text-sm text-gray-500 mb-2">
            ÏÉùÏÑ±Ïùº: {trip.created_at}
          </p>
          {/* Ï†ëÍ∑ºÏÑ± Ï†ïÎ≥¥Í∞Ä ÏûàÏùÑ Í≤ΩÏö∞ Ï†ëÍ∑ºÏÑ± Ï†ïÎ≥¥ ÌëúÏãú */}
          {trip.access && (
            <div className="flex flex-wrap gap-2">
              {Object.entries(trip.access).map(([key, value]) => (
                // Ï†ëÍ∑ºÏÑ± Ìï≠Î™©Î≥ÑÎ°ú Ïù¥Ïö© Í∞ÄÎä• Ïó¨Î∂Ä ÌëúÏãú
                <span
                  key={key}
                  className={`px-3 py-1 rounded-full text-sm font-medium ${
                    value
                      ? "bg-green-100 text-green-700" // Ïù¥Ïö© Í∞ÄÎä•
                      : "bg-gray-200 text-gray-500 line-through" // Ïù¥Ïö© Î∂àÍ∞Ä
                  }`}
                >
                  {key}
                </span>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );

  return (
    // ÌéòÏù¥ÏßÄ Ï†ÑÏ≤¥ Î†àÏù¥ÏïÑÏõÉ
    <div className="max-w-5xl mx-auto mt-10 px-4">
      {/* ÏÇ¨Ïö©Ïûê Ïù¥Î¶ÑÍ≥º Ïó¨Ìñâ Í∏∞Î°ù ÌÉÄÏù¥ÌãÄ */}
      <h1 className="text-3xl font-bold mb-6">{user.name}ÎãòÏùò Ïó¨Ìñâ Í∏∞Î°ù</h1>
      {/* Ï°∞Í±¥Î∂Ä Î†åÎçîÎßÅ: Î°úÎî© Ï§ë/Î°úÎî© ÏôÑÎ£å */}
      {loading ? (
        // Îç∞Ïù¥ÌÑ∞ Î°úÎî© Ï§ë Î©îÏãúÏßÄ
        <p>Î∂àÎü¨Ïò§Îäî Ï§ë...</p>
      ) : (
        <>
          {/* Ï∂îÏ≤ú Ïó¨Ìñâ ÏÑπÏÖò ÌÉÄÏù¥ÌãÄ */}
          <h2 className="text-2xl font-semibold mt-8 mb-4">
            üê∞ AIÏùò Ï∂îÏ≤ú Ïó¨Ìñâ
          </h2>
          {/* Ï∂îÏ≤ú Ïó¨Ìñâ Îç∞Ïù¥ÌÑ∞Í∞Ä ÏûàÏùÑ Îïå/ÏóÜÏùÑ Îïå Ï°∞Í±¥Î∂Ä Î†åÎçîÎßÅ */}
          {recommendedTrips.length > 0 ? (
            renderTrips(recommendedTrips)
          ) : (
            <p className="text-gray-500">Ï∂îÏ≤ú Î∞õÏùÄ Ïó¨ÌñâÏù¥ ÏóÜÏäµÎãàÎã§.</p>
          )}
        </>
      )}
    </div>
  );
}
